#PARNAME=rowlen #LOC=1,1 #PARNAME=collen #LOC=2,1 #HASWEIGHT
def connectionFunc( srclocs, dstlocs, rowlen, collen ):
import math
i = 0 out = [] for srcloc in srclocs: col=i%int(rowlen) row=math.floor(i/rowlen) # add the kernel # UP curr_row=row+1 curr_col=col if curr_col>=0 and curr_col<rowlen and curr_row>=0 and curr_row<collen: ind = curr_row*rowlen+curr_col conn = (i,ind,0,0.33) out.append(conn) # DOWN curr_row=row-1 curr_col=col if curr_col>=0 and curr_col<rowlen and curr_row>=0 and curr_row<collen: ind = curr_row*rowlen+curr_col conn = (i,ind,0,0.33) out.append(conn) # LEFT curr_row=row curr_col=col-1 if curr_col>=0 and curr_col<rowlen and curr_row>=0 and curr_row<collen: ind = curr_row*rowlen+curr_col conn = (i,ind,0,-0.33) out.append(conn) # TOP LEFT curr_row=row+1 curr_col=col-1 if curr_col>=0 and curr_col<rowlen and curr_row>=0 and curr_row<collen: ind = curr_row*rowlen+curr_col conn = (i,ind,0,-0.33) out.append(conn) # BOTTOM LEFT curr_row=row-1 curr_col=col-1 if curr_col>=0 and curr_col<rowlen and curr_row>=0 and curr_row<collen: ind = curr_row*rowlen+curr_col conn = (i,ind,0,-0.33) out.append(conn) # CENTRE curr_row=row curr_col=col if curr_col>=0 and curr_col<rowlen and curr_row>=0 and curr_row<collen: ind = curr_row*rowlen+curr_col conn = (i,ind,0,0.33) out.append(conn) i = i + 1 return out

