#PARNAME=rowlen #LOC=1,1 #PARNAME=collen #LOC=2,1 #PARNAME=numstripes #LOC=3,1
def connectionFunc( srclocs, dstlocs, rowlen, collen, numstripes ):
import math
i = 0 out = [] for srcloc in srclocs: col=i%int(rowlen) row=math.floor(i/rowlen) ind = math.floor(col/(rowlen/numstripes)) conn = (i,ind,0,0.33) out.append(conn) i = i + 1 return out

